<script setup>
import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

const featuresInputRef = ref(null);
const featuresRef = ref(null);
const benefitInputRef = ref(null);
const benefitImageRef = ref(null);
const business = ref([
    "Efficiency and Time Savings",
    "Centralized Data Management",
    "Remote Accessibility",
    "Data Analytics and Reporting",
    "Enhanced Security",
]);


onMounted(async () => {
    await nextTick();

   
    // Enhanced animations triggered on scroll
    gsap.from(featuresInputRef.value, {
        scrollTrigger: {
            trigger: featuresInputRef.value,
            start: "top 100%",
            end: "bottom top",
            toggleActions: "restart pause resume pause",

        },
        duration: 1.5,
        x: -100,
        autoAlpha: 0
    });


    gsap.from(benefitImageRef.value, {
        scrollTrigger: {
            trigger: benefitImageRef.value,
            start: "top 100%",
            end: "bottom top",
            toggleActions: "restart pause resume pause",

        },
        duration: 1.5,
        x: 100,
        autoAlpha: 0
    });

    gsap.from(benefitInputRef.value, {
        scrollTrigger: {
            trigger: benefitInputRef.value,
            start: "top 100%",
            end: "bottom top",
            toggleActions: "restart pause resume pause",

        },
        duration: 1.5,
        x: -100,
        autoAlpha: 0
    });

    gsap.from(featuresRef.value, {
        scrollTrigger: {
            trigger: featuresRef.value,
            start: "left 100%",
            end: "bottom top",
            toggleActions: "restart pause resume pause",
        },
        duration: 1.5,
        y: -100,
        autoAlpha: 0,
        // ease: "back.out(1.7)"
    });

   
});
</script>

